sections.css:
@import "base/body"
@import "base/paragraph"
@import "base/heading"
@import "base/blockquote"
@import "base/link"
@import "base/ordered_list"
@import "base/unordered_list"
@import "base/table"
@import "base/radio"
@import "base/checkbox"
@import "base/input"
@import "base/shared"

// Component
// --------------------------------------------------
.radio
  display: block
  margin: 15px 0

// Elements
// --------------------------------------------------
.mdl-radio__inner-circle
  background: $color-blue-medium

// Modifiers
// --------------------------------------------------
.radio--inline
  display: inline-block
  vertical-align: middle
  margin-right: 15px
  &:last-of-type
    margin-right: 0

// Dependiencies
// --------------------------------------------------
.mdl-radio__ripple-container
  .mdl-ripple
    background: $color-blue-medium

// States
// --------------------------------------------------
.mdl-radio.is-checked
  .mdl-radio__outer-circle
    border-color: $color-blue-medium

// Component
// --------------------------------------------------
.checkbox
  display: block
  margin: 15px 0

// Elements
// --------------------------------------------------
.mdl-checkbox__ripple-container
  .mdl-ripple
    background-color: $color-blue-medium

.mdl-checkbox__label
  font-size: $font-size-xsmall
  font-weight: $font-weight-thin
  display: block
  color: lighten($color-grey-medium, 10%)

// Modifiers
// --------------------------------------------------
.checkbox--inline
  display: inline-block
  width: auto
  margin-right: 15px
  vertical-align: middle
  &:last-of-type
    margin-right: 0

// Dependiencies
// --------------------------------------------------

// States
// --------------------------------------------------
.mdl-checkbox.is-checked
  .mdl-checkbox__box-outline
    border-color: $color-blue-medium
  .mdl-checkbox__tick-outline
    background-color: $color-blue-medium

@import "components/search-bar"
@import "components/header"
@import "components/header-logo"
@import "components/header-menu"
@import "components/login-menu"
